sql.where:
del_flag=0
and s.dict_name = #{dictName}
and s.dict_code = #{dictCode}
and s.display_name = #{displayName}

mapper.select:
select * from system_dict where del_flag = 0 and dict_code in
#{dictCode}
order by dict_code, display_value, modify_time desc

