sql.where:
del_flag = 0
 and appendix_name = #{appendixName}


	and concat(ifnull(appendix_name,''),ifnull(full_name,'')) like concat('%',#{pattern},'%') escape'/'

mapper.select:
select appendix_name,full_name,oss_resource_id,upload_time
from complaint_appendix
where del_flag = 0
 and complaint_id = #{complaintId}
 and id_card_flag = #{idCardFlag}
 and ort_flag = #{ortFlag}
 and enterprise_flag = #{enterpriseFlag}
 and trademark_flag = #{trademarkFlag}
