mapper.delete:
UPDATE question
set del_flag = 1
where question_id = #{questionId}

mapper.update:
update question
set
  question_content = #{questionContent,jdbcType=VARCHAR}
where del_flag = 0 and question_id = #{questionId}

mapper.select:
select

from question
where del_flag = 0 and question_id = #{questionId}

select * from question where del_flag = 0 and question_id in
#{item}
order by create_time DESC
