sql.where:
del_flag = 0

	AND type = #{type}


	AND author = #{author}


	AND title LIKE concat('%',#{title},'%')


	AND ascription = #{ascription}

mapper.select:
select id,title from news_center where del_flag = 0 AND  news_date>#{newsDate}

	and type = #{type}


	and ascription = #{ascription}

ORDER BY news_date limit 1

select id,title from news_center where del_flag = 0 AND news_date < #{newsDate}

	and type = #{type}


	and ascription = #{ascription}

ORDER BY news_date DESC limit 1
